/*.action-banner { display: none !important; }*/

.shape {
            position: relative;
            background-color: #C4DFD0;
            width: 250px;
            height: 250px;
            display: flex;
            align-items: center;
            justify-content: center;
        } 
.shape::before {
            content: '';
            position: absolute;
            bottom: 0;
            left: 0;
            width: 250px;
            height: 250px;
            background-color: white; /* same as background */
            border-color:white;
            border-radius: 250px 0 0 0;
        }
.line{
    /*margin-left:7px;*/
    left:7px;
    top:0;
    background:#4A446D;
    width:10px;
    height:50%;
    position:absolute;
}

.main-format{
    border-right:3px solid #4A446D;
    padding-right:10px;
    height:100vh;
}
.print-format{
        padding-right:5px;
        border-right:15px solid #C4DFD0;
}


